import React, { useEffect, useState } from 'react';
import { Box, Typography, Paper, Container } from '@mui/material';
import axios from 'axios';

const ViewComplaints = () => {
  const [complaints, setComplaints] = useState([]);

  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/complaints');
        setComplaints(response.data);
      } catch (error) {
        console.error('Error fetching complaints:', error);
      }
    };

    fetchComplaints();
  }, []);

  return (
    <Container>
        <Typography variant="h4" gutterBottom>View Complaints</Typography>
        <TableContainer component={Paper}>
            <Table>
                <TableHead>
                    <TableRow>
                        <TableCell>Roll No</TableCell>
                        <TableCell>Email</TableCell>
                        <TableCell>Lab</TableCell>
                        <TableCell>System Number</TableCell>
                        <TableCell>Date</TableCell>
                        <TableCell>Time</TableCell>
                        <TableCell>Complaint</TableCell>
                        <TableCell>Status</TableCell>
                        <TableCell>Actions</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {complaints.map((complaint, index) => (
                        <TableRow key={index}>
                            <TableCell>{complaint.rollNo}</TableCell>
                            <TableCell>{complaint.email}</TableCell>
                            <TableCell>{complaint.lab}</TableCell>
                            <TableCell>{complaint.systemNumber}</TableCell>
                            <TableCell>{complaint.date}</TableCell>
                            <TableCell>{complaint.time}</TableCell>
                            <TableCell>{complaint.complaint}</TableCell>
                            <TableCell>{complaint.status}</TableCell>
                            <TableCell>
                                <Button onClick={() => updateStatus(complaint._id, 'On Process')}>On Process</Button>
                                <Button onClick={() => updateStatus(complaint._id, 'Grievance Solved')}>Grievance Solved</Button>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    </Container>
);
};

export default ViewComplaints;
