import React, { useEffect, useState } from 'react';
import axios from 'axios';
/*import { useAuth } from '../context/AuthContext';*/ // Assuming you're using context for authentication

const ViewComplaintStatus = () => {
  const [complaints, setComplaints] = useState([]);
  const { token } = useAuth();  // Get token from auth context

  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/complaints', {
          headers: {
            Authorization: `Bearer ${token}`  // Pass JWT token in Authorization header
          }
        });
        setComplaints(response.data);
      } catch (error) {
        console.error('Error fetching complaints:', error);
      }
    };

    fetchComplaints();
  }, [token]);

  return (
    <div>
      {/* Render the complaints */}
      {complaints.map(complaint => (
        <div key={complaint._id}>
          <p>Complaint: {complaint.complaint}</p>
          <p>Status: {complaint.status}</p>
        </div>
      ))}
    </div>
  );
};

export default ViewComplaintStatus;
