import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Box,
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from '@mui/material';

const ViewComplaintStatus = () => {
  const [complaints, setComplaints] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        const userEmail = localStorage.getItem('email'); // Fetch user's email from local storage
        
        if (!userEmail) {
          throw new Error('User email not found in local storage.');
        }

        // Fetch complaints associated with the user's email
        const response = await axios.get('http://localhost:5000/api/complaints', {
          params: {
            email: userEmail, // Send the email in the query params
          },
        });

        if (!Array.isArray(response.data)) {
          throw new Error('Unexpected response format from server.');
        }

        setComplaints(response.data);
      } catch (error) {
        console.error('Error fetching complaints:', error);
        setError('Failed to load complaints. Please try again later.');
      }
    };

    fetchComplaints();
  }, []);

  return (
    <Container sx={{ marginTop: 8 }}>
      <Typography variant="h4" gutterBottom>
        Your Complaint Status
      </Typography>
      {error && (
        <Typography color="error" sx={{ marginTop: 2 }}>
          {error}
        </Typography>
      )}
      {complaints.length > 0 ? (
        <TableContainer component={Paper} sx={{ marginTop: 3 }}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>
                  <strong>Complaint</strong>
                </TableCell>
                <TableCell>
                  <strong>Status</strong>
                </TableCell>
                <TableCell>
                  <strong>Lab</strong>
                </TableCell>
                <TableCell>
                  <strong>System Number</strong>
                </TableCell>
                <TableCell>
                  <strong>Date</strong>
                </TableCell>
                <TableCell>
                  <strong>Time</strong>
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {complaints.map((complaint) => (
                <TableRow key={complaint._id}>
                  <TableCell>{complaint.complaint || 'N/A'}</TableCell>
                  <TableCell>{complaint.status || 'Pending'}</TableCell>
                  <TableCell>{complaint.lab || 'Unknown'}</TableCell>
                  <TableCell>{complaint.systemNumber || '-'}</TableCell>
                  <TableCell>{complaint.date || 'N/A'}</TableCell>
                  <TableCell>{complaint.time || 'N/A'}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      ) : (
        !error && (
          <Typography variant="body1" sx={{ marginTop: 2 }}>
            No complaints found.
          </Typography>
        )
      )}
    </Container>
  );
};

export default ViewComplaintStatus;
