import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../index.css'; // Import your CSS file

const HomeDescription = () => {
  const navigate = useNavigate();

  return (
    <div className="relative">
      <div className="flex flex-col lg:flex-row p-8 bg-gradient-to-blue from-blue--600 to--300">
        <div className="w-full lg:w-1/2 h-full flex items-center justify-center pt-10 pb-10">
          <img src="/images/20945431.jpg" alt="Image Component" className="h-full object-cover" />
        </div>

        <div className="flex-1 bg-white p-8 pt-10 pb-10 mt-20 mb-12 shadow-2xl shadow-blue-900 rounded-lg ml-12 max-w-xl">
          <h2 className="text-4xl font-bold text-gray-800 mb-6 leading-tight">
          Welcome to
          PSG- LabFix
          </h2>
          <p className="text-lg text-gray-700">
          Students can file complaints, track status, and receive notifications
          </p>
          <br />
          
            <div className="bottom-buttons home-bottom">
                    <button
                        className="button-main"
                        onClick={() => {
                          navigate("/Login");
                      }}
                    >
                        <span>Student</span>
                    </button>
                    &nbsp;&nbsp;
                    <button className="button-main"
                     onClick={() => {
                      navigate("/admin-login");
                  }}>                    
                        <span>Admin</span>
                    </button>
                </div>
            </div>
        
         
        </div>
      </div>
      
    
  );
};

export default HomeDescription;
