import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../index.css'; // Import your CSS file

const HomeDescription = () => {
  const navigate = useNavigate();

  return (
    <div className="relative home-container">
      <div className="content-container p-8">
        <div className="content-box shadow-2xl shadow-blue-900 rounded-lg">
          <h2 className="text-4xl font-bold text-gray-800 mb-6 leading-tight">
            Welcome to MCA- LabFix
          </h2>
          <p className="text-lg text-gray-700">
            Students can file complaints, track status, and receive notifications
          </p>
          <br />
          <div className="bottom-buttons home-bottom">
            <button
              className="button-main"
              onClick={() => {
                navigate("/Login");
              }}
            >
              <span>Student</span>
            </button>
            &nbsp;&nbsp;
            <button
              className="button-main"
              onClick={() => {
                navigate("/admin-login");
              }}
            >
              <span>Admin</span>
            </button>
          </div>
        </div>
      </div>
      <div className="marquee-container">
        <div className="marquee">
          Welcome to Lab Fix
        </div>
      </div>
    </div>
  );
};

export default HomeDescription;
