import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom'; // Import useLocation hook
import {
  Box, Container, Typography, Table, TableBody, TableCell, TableContainer,
  TableHead, TableRow, Paper,
} from '@mui/material';

const ViewComplaintStatus = () => {
  const [complaints, setComplaints] = useState([]);
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const userEmail = queryParams.get('email'); // Extract the email from the query parameters

  useEffect(() => {
    const fetchComplaints = async () => {
      if (!userEmail) return; // If no email is found, do not fetch complaints

      try {
        const response = await axios.get('http://localhost:5000/api/complaints', {
          params: {
            email: userEmail, // Send the email in the query params to fetch only the user's complaints
          },
        });

        setComplaints(response.data); // Update state with the fetched complaints
      } catch (error) {
        console.error('Error fetching complaints:', error);
      }
    };

    fetchComplaints();
  }, [userEmail]); // Add userEmail to dependencies to refetch if email changes

  return (
    <Container sx={{ marginTop: 8 }}>
      <Typography variant="h4" gutterBottom>
        Your Complaint Status
      </Typography>
      {complaints.length > 0 ? (
        <TableContainer component={Paper} sx={{ marginTop: 3 }}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell><strong>Complaint</strong></TableCell>
                <TableCell><strong>Status</strong></TableCell>
                <TableCell><strong>Lab</strong></TableCell>
                <TableCell><strong>System Number</strong></TableCell>
                <TableCell><strong>Date</strong></TableCell>
                <TableCell><strong>Time</strong></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {complaints.map((complaint) => (
                <TableRow key={complaint._id}>
                  <TableCell>{complaint.complaint}</TableCell>
                  <TableCell>{complaint.status}</TableCell>
                  <TableCell>{complaint.lab}</TableCell>
                  <TableCell>{complaint.systemNumber}</TableCell>
                  <TableCell>{complaint.date}</TableCell>
                  <TableCell>{complaint.time}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      ) : (
        <Typography variant="body1" sx={{ marginTop: 2 }}>
          No complaints found.
        </Typography>
      )}
    </Container>
  );
};

export default ViewComplaintStatus;
