import React, { useState } from 'react';
import axios from 'axios'; // Import axios
import { Box, Button, Container, Grid, TextField, Typography, MenuItem } from '@mui/material';

// Strong password validation regex
const validatePassword = (password) => {
  const regex = /^(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#$%^&*.])[A-Za-z\d@$!%*?&]{8,}$/

  return regex.test(password);
};

const Users = () => {
  const [users, setUsers] = useState([{ name: '', email: '', password: '', yearOfStudy: '' }]);
  const [errors, setErrors] = useState({}); // To store validation errors

  // Handle input changes for form fields
  const handleInputChange = (index, event) => {
    const { name, value } = event.target;

    const updatedUsers = [...users];
    updatedUsers[index][name] = value;

    // Clear the error for password field when user modifies it
    if (name === 'password') {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [index]: validatePassword(value) ? '' : 'Password must be at least 8 characters, include uppercase, lowercase, a number, and a special character.',
      }));
    }

    setUsers(updatedUsers);
  };

  // Handle dynamic addition of user fields
  const handleAddFields = () => {
    setUsers([...users, { name: '', email: '', password: '', yearOfStudy: '' }]);
  };

  // Handle dynamic removal of user fields
  const handleRemoveFields = (index) => {
    const updatedUsers = [...users];
    updatedUsers.splice(index, 1);

    setUsers(updatedUsers);

    // Remove error state for the deleted user
    setErrors((prevErrors) => {
      const newErrors = { ...prevErrors };
      delete newErrors[index];
      return newErrors;
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if there are any errors before submitting
    const hasErrors = Object.values(errors).some((error) => error);
    if (hasErrors) {
      alert('Please fix the errors before submitting.');
      return;
    }

    try {
      // Submit all users in parallel
      const submissionPromises = users.map((user) =>
        axios.post('http://localhost:5000/api/users/add', user)
      );

      await Promise.all(submissionPromises);

      alert('Users added successfully!');
      setUsers([{ name: '', email: '', password: '', yearOfStudy: '' }]); // Reset fields
      setErrors({});
    } catch (error) {
      console.error('Error adding user:', error);
      alert('Failed to add user');
    }
  };

  return (
    <Container sx={{ backgroundColor: '#566fa3', borderRadius: '8px', padding: '20px', mt: 4 }}>
      <Typography variant="h4" gutterBottom>Add Users</Typography>
      <form onSubmit={handleSubmit}>
        {users.map((user, index) => (
          <Box key={index} mb={3} p={3} sx={{ backgroundColor: '#ffffff', borderRadius: '8px', boxShadow: 2 }}>
            <Grid container spacing={4}>
              {/* Name Field */}
              <Grid item xs={12} md={3}>
                <TextField
                  fullWidth
                  label="Name"
                  name="name"
                  value={user.name}
                  onChange={(event) => handleInputChange(index, event)}
                  required
                  InputLabelProps={{
                    shrink: true,
                  }}
                  placeholder="Enter name"
                />
              </Grid>

              {/* Email Field */}
              <Grid item xs={12} md={3}>
                <TextField
                  fullWidth
                  label="Email"
                  type="email"
                  name="email"
                  value={user.email}
                  onChange={(event) => handleInputChange(index, event)}
                  required
                  InputLabelProps={{
                    shrink: true,
                  }}
                  placeholder="Enter email"
                />
              </Grid>

              {/* Password Field with error handling */}
              <Grid item xs={12} md={3}>
                <TextField
                  fullWidth
                  label="Password"
                  type="password"
                  name="password"
                  value={user.password}
                  onChange={(event) => handleInputChange(index, event)}
                  required
                  error={!!errors[index]} // Show error if password is invalid
                  helperText={errors[index]} // Display error message
                  placeholder="Enter password"
                />
              </Grid>

              {/* Dropdown Field */}
              <Grid item xs={12} md={3}>
                <TextField
                  select
                  fullWidth
                  label="Year of Study"
                  name="yearOfStudy"
                  value={user.yearOfStudy}
                  onChange={(event) => handleInputChange(index, event)}
                  required
                >
                  <MenuItem value="1st year">1st Year</MenuItem>
                  <MenuItem value="2nd year">2nd Year</MenuItem>
                </TextField>
              </Grid>
            </Grid>

            {/* Button to Remove Field */}
            <Box mt={2}>
              <Button
                variant="outlined"
                color="secondary"
                onClick={() => handleRemoveFields(index)}
                disabled={users.length === 1}
              >
                REMOVE
              </Button>
            </Box>
          </Box>
        ))}

        {/* Buttons for form submission */}
        <Box mt={4} className="flex justify-between">
          <Button variant="contained" color="primary" onClick={handleAddFields}>
            Add User
          </Button>
          <Button variant="contained" color="secondary" type="submit">
            Submit
          </Button>
        </Box>
      </form>
    </Container>
  );
};

export default Users;
