import React, { useState } from 'react';
import { Box, Button, Container, Grid, TextField, Typography, MenuItem } from '@mui/material';

const Users = () => {
  const [users, setUsers] = useState([{ name: '', email: '', password: '', yearOfStudy: '' }]);

  const handleInputChange = (index, event) => {
    const values = [...users];
    values[index][event.target.name] = event.target.value;
    setUsers(values);
  };

  const handleAddFields = () => {
    setUsers([...users, { name: '', email: '', password: '', yearOfStudy: '' }]);
  };

  const handleRemoveFields = (index) => {
    const values = [...users];
    values.splice(index, 1);
    setUsers(values);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/users/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(users),
      });

      const result = await response.json();

      if (response.ok) {
        alert('Users added successfully');
      } else {
        alert(`Error: ${result.message}`);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error adding users');
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Add Users</Typography>
      <form onSubmit={handleSubmit}>
        {users.map((user, index) => (
          <Box key={index} mb={3} className="p-6 bg-background rounded-lg shadow-lg">
            <Grid container spacing={2}>
              <Grid item xs={12} md={3} className="flex items-center">
                <label htmlFor="name" className="w-1/4 text-muted-foreground">Name *</label>
                <TextField
                  fullWidth
                  type="text"
                  name="name"
                  id="name"
                  value={user.name}
                  onChange={(event) => handleInputChange(index, event)}
                  required
                  InputLabelProps={{
                    shrink: true,
                  }}
                  className="flex-1 border border-border rounded-md p-2"
                  placeholder="Enter name"
                />
              
              </Grid>

              <Grid item xs={12} md={3} className="flex items-center">
                <label htmlFor="email" className="w-1/4 text-muted-foreground">Email *</label>
                <TextField
                  fullWidth
                  type="email"
                  name="email"
                  id="email"
                  value={user.email}
                  onChange={(event) => handleInputChange(index, event)}
                  required
                  InputLabelProps={{
                    shrink: true,
                  }}
                  className="flex-1 border border-border rounded-md p-2 bg-muted"
                  placeholder="Enter email"
                />
              </Grid>

              <Grid item xs={12} md={3} className="flex items-center">
                <label htmlFor="password" className="w-1/4 text-muted-foreground">Password *</label>
                <TextField
                  fullWidth
                  type="password"
                  name="password"
                  id="password"
                  value={user.password}
                  onChange={(event) => handleInputChange(index, event)}
                  required
                  InputLabelProps={{
                    shrink: true,
                  }}
                  className="flex-1 border border-border rounded-md p-2 bg-muted"
                  placeholder="Enter password"
                />
              </Grid>

              <Grid item xs={12} md={3} className="flex items-center">
                <label htmlFor="yearOfStudy" className="w-1/4 text-muted-foreground">Year of Study *</label>
                <TextField
                  select
                  fullWidth
                  name="yearOfStudy"
                  id="yearOfStudy"
                  value={user.yearOfStudy}
                  onChange={(event) => handleInputChange(index, event)}
                  required
                  InputLabelProps={{
                    shrink: true,
                  }}
                  className="flex-1 border border-border rounded-md p-2 bg-muted"
                >
                  <MenuItem value="1st year">1st Year</MenuItem>
                  <MenuItem value="2nd year">2nd Year</MenuItem>
                </TextField>
                
              </Grid>
            </Grid>
          </Box>
        ))}
        <Box mt={2} className="mt-6 flex justify-between">
          <Button variant="contained" color="primary" onClick={handleAddFields} className="bg-primary text-primary-foreground hover:bg-primary/80 rounded-md p-2">
            Add User
          </Button>
          <Button variant="contained" color="secondary" type="submit" className="bg-secondary text-secondary-foreground hover:bg-secondary/80 rounded-md p-2">
            Submit
          </Button>
        </Box>
      </form>
    </Container>
  );
};

export default Users;
