import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../index.css'; // Ensure this includes Tailwind CSS

const HomeDescription = () => {
  const navigate = useNavigate();

  return (
    <div className="relative h-screen flex flex-col justify-center items-start bg-cover bg-center bg-no-repeat" style={{ backgroundImage: "url('/images/your-background-image.jpg')" }}>
      <div className="max-w-lg p-8 bg-white bg-opacity-90 shadow-2xl rounded-lg ml-12 z-10">
        <h2 className="text-4xl font-bold text-gray-800 mb-6 leading-tight">
          Welcome to MCA- LabFix
        </h2>
        <p className="text-lg text-gray-700">
          Students can file complaints, track status, and receive notifications
        </p>
        <div className="mt-6 flex space-x-4">
          <button
            className="button-main"
            onClick={() => navigate("/Login")}
          >
            <span>Student</span>
          </button>
          <button
            className="button-main"
            onClick={() => navigate("/admin-login")}
          >
            <span>Admin</span>
          </button>
        </div>
      </div>
      <div className="custom-clip-path"></div>
      <div className="marquee-container">
        <div className="marquee">
          Welcome to Lab Fix
        </div>
      </div>
    </div>
  );
};

export default HomeDescription;
