import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // You need axios for making HTTP requests
import "../styles/forms.css";
import leftImage from '../assets/logo-Photoroom1.png';

const AdminLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });
      
      const { token, role } = response.data;

      if (role === 'admin') {
        localStorage.setItem('token', token);
        navigate('/admin-home');
      } else {
        alert('Not authorized as admin');
      }
    } catch (error) {
      alert('Invalid credentials');
    }
  };

  return (
    <div className="storage-section">
      <div className="leftsection">
        <img src={leftImage} alt="Left Section" className="left-image" />
      </div>
      <div className="rightsection">
        <h1 className="h1-line-height" style={{ fontSize: '45px' }}>Welcome Admin</h1>
        <br />
        <div className="input-form">
          <div className="formsgroup">
            <label htmlFor="inputEmail">Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email-id"
              className="formscontrol"
              id="inputEmail"
            />
          </div>
          <div className="formsgroup">
            <label htmlFor="inputPassword">Password</label>
            <input
              type={showPassword ? 'text' : 'password'}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              className="formscontrol"
              id="inputPassword"
            />
            <br />
            <br />
            <input
              type="checkbox"
              id="showPassword"
              checked={showPassword}
              onChange={() => setShowPassword(!showPassword)}
            />
            <label htmlFor="showPassword">Show Password</label>
          </div>
          <div className="bottom-buttons">
            <button className="button-main" onClick={handleLogin}>Log In</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
