import React, { useEffect, useState } from 'react';
import {
  Box, Container, Typography, Table, TableBody, TableCell, TableContainer,
  TableHead, TableRow, Paper, MenuItem, Select, FormControl, InputLabel,
  Modal, Button, Divider, Tooltip
} from '@mui/material';
import { styled } from '@mui/system';
import axios from 'axios';
import AdminNavbar from '../components/AdminNavbar';

// Custom styles for the TableRow
const StyledTableRow = styled(TableRow)(({ theme }) => ({
  cursor: 'pointer',
  '&:hover': {
    backgroundColor: theme.palette.action.hover,
  },
}));

// Modal styling
const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  bgcolor: 'background.paper',
  borderRadius: 4,
  boxShadow: 24,
  p: 4,
  width: 500,
};

const ViewComplaints = () => {
  const [complaints, setComplaints] = useState([]);
  const [filteredComplaints, setFilteredComplaints] = useState([]);
  const [selectedLab, setSelectedLab] = useState('');
  const [open, setOpen] = useState(false);
  const [selectedComplaint, setSelectedComplaint] = useState(null);

  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/complaints');
        setComplaints(response.data);
        setFilteredComplaints(response.data);
      } catch (error) {
        console.error('Error fetching complaints:', error);
      }
    };

    fetchComplaints();
  }, []);

  useEffect(() => {
    if (selectedLab === '') {
      setFilteredComplaints(complaints);
    } else {
      setFilteredComplaints(complaints.filter((complaint) => complaint.lab === selectedLab));
    }
  }, [selectedLab, complaints]);

  const handleLabChange = (event) => {
    setSelectedLab(event.target.value);
  };

  const handleOpen = (complaint) => {
    setSelectedComplaint(complaint);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setSelectedComplaint(null);
  };

  const handleStatusUpdate = async (status) => {
    try {
      const response = await axios.put(`http://localhost:5000/api/complaints/${selectedComplaint._id}`, { status });
      const updatedComplaint = response.data;

      setComplaints((prevComplaints) =>
        prevComplaints.map((complaint) =>
          complaint._id === updatedComplaint._id ? updatedComplaint : complaint
        )
      );

      handleClose();
    } catch (error) {
      console.error('Error updating complaint status:', error);
    }
  };

  return (
    <div>
      <AdminNavbar />
      <Container className="mt-32" sx={{ marginTop: 8 }}>
        <Typography variant="h4" gutterBottom>
          View Complaints
        </Typography>
        <Box display="flex" justifyContent="flex-end" mb={2}>
          <FormControl variant="outlined" style={{ minWidth: 200 }}>
            <InputLabel id="lab-filter-label">Filter by Lab</InputLabel>
            <Select
              labelId="lab-filter-label"
              value={selectedLab}
              onChange={handleLabChange}
              label="Filter by Lab"
            >
              <MenuItem value="">
                <em>All</em>
              </MenuItem>
              <MenuItem value="CC">CC</MenuItem>
              <MenuItem value="ISL">ISL</MenuItem>
              <MenuItem value="Project Lab">Project Lab</MenuItem>
              <MenuItem value="IBM">IBM</MenuItem>
            </Select>
          </FormControl>
        </Box>
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Roll No</TableCell>
                <TableCell>Email</TableCell>
                <TableCell>Lab</TableCell>
                <TableCell>System Number</TableCell>
                <TableCell>Date</TableCell>
                <TableCell>Time</TableCell>
                <TableCell>Complaint</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {filteredComplaints.map((complaint) => (
                <StyledTableRow key={complaint._id} onClick={() => handleOpen(complaint)}>
                  <TableCell>{complaint.rollNo}</TableCell>
                  <TableCell>{complaint.email}</TableCell>
                  <TableCell>{complaint.lab}</TableCell>
                  <TableCell>{complaint.systemNumber}</TableCell>
                  <TableCell>{complaint.date}</TableCell>
                  <TableCell>{complaint.time}</TableCell>
                  <TableCell>
                    <Tooltip title={complaint.complaint} arrow>
                      <span>{complaint.complaint.substring(0, 30)}...</span>
                    </Tooltip>
                  </TableCell>
                </StyledTableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Container>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="complaint-modal-title"
        aria-describedby="complaint-modal-description"
      >
        <Box sx={style}>
          <Typography id="complaint-modal-title" variant="h6" component="h2">
            Complaint Details
          </Typography>
          <Divider sx={{ my: 2 }} />
          {selectedComplaint && (
            <div>
              <Typography>
                <strong>Roll No:</strong> {selectedComplaint.rollNo}
              </Typography>
              <Typography>
                <strong>Email:</strong> {selectedComplaint.email}
              </Typography>
              <Typography>
                <strong>Lab:</strong> {selectedComplaint.lab}
              </Typography>
              <Typography>
                <strong>System Number:</strong> {selectedComplaint.systemNumber}
              </Typography>
              <Typography>
                <strong>Date:</strong> {selectedComplaint.date}
              </Typography>
              <Typography>
                <strong>Time:</strong> {selectedComplaint.time}
              </Typography>
              <Typography>
                <strong>Complaint:</strong> {selectedComplaint.complaint}
              </Typography>
              <Box display="flex" justifyContent="space-between" mt={4}>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => handleStatusUpdate('On Process')}
                >
                  Mark as On Process
                </Button>
                <Button
                  variant="contained"
                  color="success"
                  onClick={() => handleStatusUpdate('Grievance Solved')}
                >
                  Mark as Solved
                </Button>
              </Box>
            </div>
          )}
        </Box>
      </Modal>
    </div>
  );
};

export default ViewComplaints;
