import React, { useEffect, useState } from 'react';
import {
  Box, Container, Typography, Table, TableBody, TableCell, TableContainer,
  TableHead, TableRow, Paper
} from '@mui/material';
import axios from 'axios';
import StudentNavbar from '../components/StudentNavbar';  // Assuming you have a navbar for students

const ViewComplaintStatus = () => {
  const [complaints, setComplaints] = useState([]);

  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/complaints');
        setComplaints(response.data);
      } catch (error) {
        console.error('Error fetching complaints:', error);
      }
    };

    fetchComplaints();
  }, []);

  return (
    <div>
      <StudentNavbar />
      <Container className="mt-32">
        <Typography variant="h4" gutterBottom>View Complaint Status</Typography>
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Roll No</TableCell>
                <TableCell>Email</TableCell>
                <TableCell>Lab</TableCell>
                <TableCell>System Number</TableCell>
                <TableCell>Complaint</TableCell>
                <TableCell>Status</TableCell> {/* Add a status column */}
              </TableRow>
            </TableHead>
            <TableBody>
              {complaints.map((complaint, index) => (
                <TableRow key={index}>
                  <TableCell>{complaint.rollNo}</TableCell>
                  <TableCell>{complaint.email}</TableCell>
                  <TableCell>{complaint.lab}</TableCell>
                  <TableCell>{complaint.systemNumber}</TableCell>
                  <TableCell>{complaint.complaint}</TableCell>
                  <TableCell>{complaint.status}</TableCell> {/* Display complaint status */}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Container>
    </div>
  );
};

export default ViewComplaintStatus;
