import React, { useState, useEffect } from 'react';
import {
    Box, Container, Grid, Stack, TextField, Typography, Paper, Snackbar, Alert, MenuItem,
    FormControl, InputLabel, Select, Checkbox, FormControlLabel
} from '@mui/material';
import { BlueButton } from '../components/buttonStyles';
import axios from 'axios';
import psg from '../assets/psg.webp';

const Complain = () => {
    const [form, setForm] = useState({
        rollNo: '',
        email: '',
        type: '',
        expertise: '',
        complaint: '',
        anonymous: false, // Checkbox for anonymous submission
        file: null, // File upload
    });
    const [open, setOpen] = useState(false);
    const [message, setMessage] = useState('');
    const [severity, setSeverity] = useState('success');

    useEffect(() => {
        const loggedInUserEmail = localStorage.getItem('userEmail') || '';
        const userRollNo = loggedInUserEmail.slice(0, 7); // Extract roll number from email

        setForm((prevForm) => ({
            ...prevForm,
            email: loggedInUserEmail,
            rollNo: userRollNo,
        }));
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm((prevForm) => ({
            ...prevForm,
            [name]: value,
        }));
    };

    const handleFileChange = (e) => {
        setForm((prevForm) => ({
            ...prevForm,
            file: e.target.files[0],
        }));
    };

    const handleAnonymousChange = (e) => {
        const isAnonymous = e.target.checked;
        setForm((prevForm) => ({
            ...prevForm,
            anonymous: isAnonymous,
            rollNo: isAnonymous ? '' : prevForm.rollNo,
            email: isAnonymous ? '' : prevForm.email,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if(!form.anonymous&&(form.rollNo||!form.email)){
            setMessage('Rollno and Email are required');
            setSeverity('error');
            setOpen(true);
            return;
        }

        try {
            const formData = new FormData();
            Object.entries(form).forEach(([key, value]) => {
                if (key === 'file' && value) {
                    formData.append(key, value); // Append file separately
                } else if (key !== 'file') {
                    formData.append(key, value);
                }
            });

            const response = await axios.post('http://localhost:5000/api/complaints', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
                timeout: 10000,
            });

            setMessage(response.data.message || 'Complaint registered successfully');
            setSeverity('success');
            setOpen(true);

            // Reset the form
            setForm((prevForm) => ({
                rollNo: prevForm.anonymous ? '' : prevForm.rollNo,
                email: prevForm.anonymous ? '' : prevForm.email,
                type: '',
                expertise: '',
                complaint: '',
                anonymous: prevForm.anonymous,
                file: null,
            }));
        } catch (error) {
            setMessage(error.response?.data?.message || 'Error registering complaint');
            setSeverity('error');
            setOpen(true);
            console.error('Error:', error);
        }
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <Container
            sx={{
                height: '100vh',
                backgroundImage: `url(${psg})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
            }}
        >
            <Grid container sx={{ py: '100px', px: 5 }} spacing={2} justifyContent="center">
                <Grid item xs={12} md={8}>
                    <Paper elevation={6} sx={{ p: 4 }}>
                        <Box sx={{ maxWidth: 550, width: '100%' }}>
                            <form onSubmit={handleSubmit}>
                                <Stack spacing={5} sx={{ mb: 4 }}>
                                    <Typography variant="h4" align="center">Register Complaint</Typography>
                                </Stack>
                                <Grid container spacing={2}>
                                    {!form.anonymous && (
                                        <>
                                            <Grid item xs={12} sm={10}>
                                                <TextField
                                                    fullWidth
                                                    label="Roll No"
                                                    name="rollNo"
                                                    value={form.rollNo}
                                                    onChange={handleChange}
                                                    InputLabelProps={{ shrink: true }}
                                                />
                                            </Grid>
                                            <Grid item xs={12} sm={10}>
                                                <TextField
                                                    fullWidth
                                                    label="Email"
                                                    name="email"
                                                    value={form.email}
                                                    onChange={handleChange}
                                                    InputLabelProps={{ shrink: true }}
                                                />
                                            </Grid>
                                        </>
                                    )}
                                    <Grid item xs={12} sm={6}>
                                        <FormControl fullWidth required>
                                            <InputLabel>Lab</InputLabel>
                                            <Select
                                                name="lab"
                                                value={form.lab}
                                                onChange={handleChange}
                                                label="Lab"
                                            >
                                                <MenuItem value="CC">CC</MenuItem>
                                                <MenuItem value="ISL">ISL</MenuItem>
                                                <MenuItem value="Project Lab">Project Lab</MenuItem>
                                                <MenuItem value="IBM">IBM</MenuItem>
                                            </Select>
                                        </FormControl>
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <FormControl fullWidth required>
                                            <InputLabel>System Number</InputLabel>
                                            <Select
                                                name="systemNumber"
                                                value={form.systemNumber}
                                                onChange={handleChange}
                                                label="System Number"
                                            >
                                                {Array.from({ length: 60 }, (_, index) => (
                                                    <MenuItem key={index + 1} value={index + 1}>
                                                        {index + 1}
                                                    </MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Complaint"
                                            name="complaint"
                                            value={form.complaint}
                                            onChange={handleChange}
                                            required
                                            multiline
                                            maxRows={4}
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <FormControlLabel
                                            control={
                                                <Checkbox
                                                    checked={form.anonymous}
                                                    onChange={handleAnonymousChange}
                                                />
                                            }
                                            label="Register as Anonymous"
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            type="file"
                                            accept="image/*"
                                            id='file'
                                            name="file"
                                            fullWidth
                                            InputLabelProps={{ shrink: true }}
                                            onChange={handleFileChange}
                                        />
                                    </Grid>
                                </Grid>
                                <Box display="flex" justifyContent="center" sx={{ mt: 3 }}>
                                    <BlueButton size="large" variant="contained" type="submit">
                                        Submit
                                    </BlueButton>
                                </Box>
                            </form>
                        </Box>
                    </Paper>
                </Grid>
            </Grid>
            <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
                <Alert onClose={handleClose} severity={severity} sx={{ width: '100%' }}>
                    {message}
                </Alert>
            </Snackbar>
        </Container>
    );
};

export default Complain;
