import React, { useState } from 'react';
import { Box, Button, Container, Grid, Stack, TextField, Typography, Snackbar, Alert } from '@mui/material';
import axios from 'axios';

const AddLab = () => {
  const [labData, setLabData] = useState({
    labName: '',
    description: ''
  });
  const [open, setOpen] = useState(false);
  const [message, setMessage] = useState('');
  const [severity, setSeverity] = useState('success');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLabData({
      ...labData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/labs/add', labData);
      setMessage('Lab added successfully');
      setSeverity('success');
      setOpen(true);
      setLabData({ labName: '', description: '' }); // Clear form after submission
    } catch (error) {
      setMessage('Error adding lab');
      setSeverity('error');
      setOpen(true);
      console.error('Error adding lab:', error);
    }
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    
    <Container maxWidth="sm">
      <Box sx={{ mt: 5 }}>
        <Typography variant="h4" align="center" gutterBottom>
          Add New Lab
        </Typography>
        <form onSubmit={handleSubmit}>
          <Stack spacing={3}>
            <TextField
              fullWidth
              label="Lab Name"
              name="labName"
              value={labData.labName}
              onChange={handleChange}
              required
            />
            <TextField
              fullWidth
              label="Lab Description"
              name="description"
              value={labData.description}
              onChange={handleChange}
              multiline
              rows={3}
              required
            />
            <Button variant="contained" color="primary" type="submit">
              Add Lab
            </Button>
          </Stack>
        </form>
        <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
          <Alert onClose={handleClose} severity={severity}>
            {message}
          </Alert>
        </Snackbar>
      </Box>
    </Container>
  );
};

export default AddLab;
