import React, { useEffect, useState } from 'react';
import { Box, Typography, Paper, Container } from '@mui/material';
import axios from 'axios';

const ViewComplaints = () => {
  const [complaints, setComplaints] = useState([]);

  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/complaints');
        setComplaints(response.data);
      } catch (error) {
        console.error('Error fetching complaints:', error);
      }
    };

    fetchComplaints();
  }, []);

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        View Complaints
      </Typography>
      {complaints.map((complaint) => (
        <Paper key={complaint._id} style={{ marginBottom: '20px', padding: '15px' }}>
          <Typography variant="body1">Roll No: {complaint.rollNo}</Typography>
          <Typography variant="body1">Email: {complaint.email}</Typography>
          <Typography variant="body1">Lab: {complaint.lab}</Typography>
          <Typography variant="body1">System Number: {complaint.systemNumber}</Typography>
          <Typography variant="body1">Date: {complaint.date}</Typography>
          <Typography variant="body1">Time: {complaint.time}</Typography>
          <Typography variant="body1">Complaint: {complaint.complaint}</Typography>
        </Paper>
      ))}
    </Container>
  );
};

export default ViewComplaints;
