import React, { useEffect, useState } from 'react';
import { Text, StyleSheet, View, FlatList } from 'react-native';
import axios from 'axios';
const ViewComplaints = () => {
  const [complaints, setComplaints] = useState([]);

  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/complaints');
        setComplaints(response.data);
      } catch (error) {
        console.error('Error fetching complaints:', error);
      }
    };

    fetchComplaints();
  }, []);

  const renderComplaint = ({ item }) => (
   
    <View style={styles.complaintContainer}>
      <Text style={styles.textLabel}>Roll No: {item.rollNo}</Text>
      <Text style={styles.textLabel}>Email: {item.email}</Text>
      <Text style={styles.textLabel}>Lab: {item.lab}</Text>
      <Text style={styles.textLabel}>System Number: {item.systemNumber}</Text>
      <Text style={styles.textLabel}>Date: {item.date}</Text>
      <Text style={styles.textLabel}>Time: {item.time}</Text>
      <Text style={styles.textLabel}>Complaint: {item.complaint}</Text>
    </View>
  );

  return (
    
    <View style={styles.container}>
      <FlatList
        data={complaints}
        renderItem={renderComplaint}
        keyExtractor={(item) => item._id} // Assuming MongoDB provides an _id field
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#f5f5f5',
  },
  complaintContainer: {
    marginBottom: 20,
    padding: 15,
    backgroundColor: '#fff',
    borderRadius: 8,
    shadowColor: '#000',
    shadowOpacity: 0.1,
    shadowOffset: { width: 0, height: 2 },
    shadowRadius: 8,
    elevation: 2,
  },
  textLabel: {
    fontSize: 16,
    marginBottom: 5,
  },
});

export default ViewComplaints;
