import React, { useEffect, useState } from 'react';
import axios from 'axios';

export const TechnicianComplaints = () => {
  const [complaints, setComplaints] = useState([]);

  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        const technicianId = localStorage.getItem('technicianId');

        if (!technicianId) {
          console.error('Technician ID not found in local storage');
          return;
        }

        console.log("Fetching complaints for technician ID:", technicianId);
        const response = await axios.get(`http://localhost:5000/api/complaints/technician?technicianId=${technicianId}`);

        setComplaints(response.data);
      } catch (error) {
        console.error('Error fetching complaints:', error);
      }
    };

    fetchComplaints();
  }, []);

  return (
    <div>
      <h1>Assigned Complaints</h1>
      {complaints.length > 0 ? (
        <ul>
          {complaints.map(complaint => (
            <li key={complaint._id}>
              <h3>Complaint: {complaint.complaint}</h3>
              <p>Lab: {complaint.lab}</p>
              <p>System Number: {complaint.systemNumber}</p>
              <p>Status: {complaint.status}</p>
              <p>Severity: {complaint.severity}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No complaints assigned to you.</p>
      )}
    </div>
  );
};
