import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TechnicianComplaints = ({ technicianId }) => {
  const [complaints, setComplaints] = useState([]);

  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        console.log("Fetching complaints for technician ID:", technicianId);
        const response = await axios.get(`/api/complaints/technician?technicianId=${technicianId}`);
        setComplaints(response.data);
      } catch (error) {
        console.error("Error fetching complaints:", error.response ? error.response.data : error.message);
      }
    };

    fetchComplaints();
  }, [technicianId]);

  return (
    <div>
      <h2>Assigned Complaints</h2>
      {complaints.length === 0 ? (
        <p>No complaints assigned to you.</p>
      ) : (
        <ul>
          {complaints.map((complaint) => (
            <li key={complaint._id}>{complaint.complaint} - {complaint.status}</li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TechnicianComplaints;
